import axios from 'axios';
export default {
  name: 'KanbanBoard',
  data() {
    return {
      board: null,
      columnNames: [] // vai preencher dinamicamente
    };
  },
  methods: {
    async loadBoard() {
      try {
        const response = await axios.get('http://localhost:8000/api/api/board/1/');
        this.board = response.data;

        // Extrair categorias únicas para usar como nomes de coluna
        const categories = new Set();
        this.board.cards.forEach(card => {
          if (card.category) categories.add(card.category);
        });
        this.columnNames = Array.from(categories);
      } catch (error) {
        console.error('Erro ao carregar o board:', error);
      }
    },
    filteredCards(columnName) {
      if (!this.board || !this.board.cards) return [];
      // Filtra pelos cards cuja categoria é igual ao nome da coluna
      return this.board.cards.filter(card => card.category === columnName);
    }
  },
  mounted() {
    this.loadBoard();
  }
};
